declare module ReactNativePaper {
    interface ThemeColors {
        /** appbar文字颜色 */
        textPrimary?: string,
        /** 普通文字 */
        text: string;
        /** 副标题文字颜色 */
        textSecondary?: string;
        /** 高亮文本颜色 */
        textHighlight?: string;
        /** 背景高亮颜色 */
        backgroundHighlight?: string;
        /** 提示文本 */
        textPlaceholder?: string;
        /** 页面背景 */
        pageBackground?: string;

    }
}